import React from 'react'

class AddFolder extends React.Component {

    state = {
        value: ''
    }

    folderName = folder => {
        this.setState({
            value: folder
        })
    }

    handleAddFolder = e => {
        e.preventDefault()
        fetch('http://localhost:3000/folders', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ name: this.state.value })
        })
    }

    render() {
        return (
            <div>
                <form onSubmit={(e) => {this.handleAddFolder(e)}}>
                    <label htmlFor="new-folder">New Folder</label>
                    <input type="text"
                            id="new-folder"
                            value={this.state.value}
                            onChange={e => this.folderName(e.target.value)}
                    />
                    <button type='button'>Add New Folder</button>
                </form>
            </div>
        )
    }
}

export default AddFolder
