import React from 'react'

class AddNote extends React.Component {

    addNote = e => {
        e.preventDefault();
        fetch('http://localhost:3000/notes', {
            method: 'POST',
            headers: {
                'Content-type': 'application/json',
            },
            body: JSON.stringify({
                name: this.state.name,
                content: this.state.content
            }),
        })
    }

    state = {
        name: '',
        content: ''
    }

    render() {
        return(

            <form onSubmit={e => { this.addNote(e) }}>
                <label htmlFor="name">Name</label>
                <input id="name" type="text" value={this.state.name} onChange={e => this.setState({ name: e.target.value })}></input>
                <label htmlFor="content">Content</label>
                <input id="content" type="text" value={this.state.content} onChange={e => this.setState({ content: e.target.value })}></input>
                <button type="submit">Add Note</button>

            </form>

        )
    }
}

export default AddNote
